// Extract data from webhook body
const webhookData = $input.first().json;
const body = webhookData.body || {};

return {
  pdfUrl: body.pdfUrl || '',
  outputSheetName: body.outputName || 'SEO Audit Report - ' + new Date().toISOString().slice(0, 10)
};

// Extract Google Drive file ID from URL
const pdfUrl = $input.first().json.pdfUrl || '';
let fileId = '';

if (!pdfUrl) {
  throw new Error('PDF URL is empty. Please provide a valid Google Drive URL.');
}

// Handle different Google Drive URL formats
if (pdfUrl.includes('/file/d/')) {
  fileId = pdfUrl.split('/file/d/')[1].split('/')[0];
} else if (pdfUrl.includes('id=')) {
  fileId = pdfUrl.split('id=')[1].split('&')[0];
} else if (pdfUrl.includes('drive.google.com/open')) {
  fileId = pdfUrl.split('id=')[1];
}

if (!fileId) {
  throw new Error('Could not extract file ID from URL: ' + pdfUrl);
}

return {
  fileId: fileId,
  outputSheetName: $input.first().json.outputSheetName
};

// Build OpenAI Responses API request using uploaded file_id.
// The PDF was uploaded via Files API; now we reference it by ID.
const uploadResult = $input.first().json;
const fileId = uploadResult.id;
const outputSheetName = $('Extract File ID').first().json.outputSheetName;

if (!fileId) {
  throw new Error('File upload failed — no file ID returned. Response: ' + JSON.stringify(uploadResult).substring(0, 500));
}

// JSON template that AI must fill in
var tpl = {
  generalInfo: { siteUrl: '', auditDate: '', scanMode: '', parametersAnalyzed: '' },
  summary: {
    totalUrlsChecked: 0,
    urlBreakdown: [{ type: '', count: 0, percent: '' }],
    urlsWithErrors: { total: 0, percent: '', breakdown: [{ type: '', count: 0, percent: '' }] }
  },
  contentTypes: [{ type: '', count: 0, percent: '' }],
  topHosts: [{ host: '', urlCount: 0, percent: '' }],
  urlStructure: [{ host: '', segment: '', count: 0, percent: '' }],
  serverResponseCodes: [{ code: '', description: '', count: 0, percent: '' }],
  indexability: {
    searchOpenness: [{ status: '', count: 0, percent: '' }],
    closureReasons: [{ reason: '', count: 0, percent: '' }]
  },
  metaRobots: [{ directive: '', count: 0, percent: '' }],
  canonicalUsage: [{ status: '', count: 0, percent: '' }],
  urlDepth: [{ depth: 0, count: 0 }],
  urlNesting: [{ nesting: 0, count: 0 }],
  loadingSpeedHtml: { maxMs: 0, minMs: 0, medianMs: 0, distribution: [{ category: '', count: 0, percent: '' }] },
  loadingSpeedResources: { maxMs: 0, minMs: 0, medianMs: 0, distribution: [{ category: '', count: 0, percent: '' }] },
  protocols: {
    html: [{ protocol: '', count: 0, percent: '' }],
    resources: [{ protocol: '', count: 0, percent: '' }]
  },
  seoElements: {
    uniqueness: [{ element: '', unique: '', duplicated: '', missing: '' }],
    length: [{ element: '', optimal: '', short: '', long: '' }]
  },
  contentMetrics: {
    charactersPerPage: [{ category: '', count: 0, percent: '' }],
    wordsPerPage: [{ category: '', count: 0, percent: '' }],
    imageSizes: [{ category: '', count: 0, percent: '' }]
  },
  errors: {
    criticality: { high: 0, medium: 0, low: 0 },
    topErrors: [{ error: '', count: 0, percent: '' }],
    highCriticality: [{ error: '', exampleUrl: '', count: 0 }],
    mediumCriticality: [{ error: '', exampleUrl: '', count: 0 }],
    lowCriticality: [{ error: '', exampleUrl: '', count: 0 }],
    notDetected: ['']
  },
  scanSettings: {
    analyzedData: { scannedUrls: '', segmentation: '', parametersSelected: '' },
    speedSettings: { maxTimeout: '', jsRendering: '' },
    crawlInstructions: {},
    crawlLimits: {},
    generalSettings: {},
    advancedSettings: {}
  }
};

var systemPrompt = 'Ти експерт з парсингу SEO-аудит звітів. Тобі надано PDF-файл технічного SEO-аудиту. Прочитай його та витягни ВСІ дані, структурувавши їх у форматі JSON.\n\nВАЖЛИВО:\n1. Витягни АБСОЛЮТНО ВСІ числові дані, відсотки, URL-приклади\n2. НЕ пропускай жодного розділу чи підрозділу\n3. Зберігай точність чисел та відсотків\n4. Для помилок завжди включай приклад URL якщо він є\n5. Відповідь має бути ТІЛЬКИ валідним JSON без markdown\n6. Ігноруй будь-які згадки брендів ПЗ (Netpeak, Screaming Frog тощо)';

var userPrompt = 'Проаналізуй цей SEO-аудит PDF та витягни ВСІ дані у такому JSON форматі:\n\n' + JSON.stringify(tpl, null, 2);

var requestBody = {
  model: 'gpt-4o',
  input: [
    { role: 'developer', content: systemPrompt },
    {
      role: 'user',
      content: [
        {
          type: 'input_file',
          file_id: fileId
        },
        { type: 'input_text', text: userPrompt }
      ]
    }
  ],
  temperature: 0.1,
  max_output_tokens: 16000
};

return {
  requestBody: requestBody,
  openaiFileId: fileId,
  outputSheetName: outputSheetName,
  timestamp: new Date().toISOString()
};

// Parse AI response from OpenAI Responses API
const response = $input.first().json;

// Responses API: output[].content[].text
let aiContent = '';
if (response.output && response.output.length > 0) {
  const msg = response.output.find(function(o) { return o.type === 'message'; });
  if (msg && msg.content && msg.content.length > 0) {
    const textBlock = msg.content.find(function(c) { return c.type === 'output_text'; });
    if (textBlock) aiContent = textBlock.text;
  }
}

if (!aiContent) {
  throw new Error('Empty AI response. Full response: ' + JSON.stringify(response).substring(0, 1000));
}

const outputSheetName = $('Build AI Request').first().json.outputSheetName;

let parsedData;
try {
  let cleanJson = aiContent.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  parsedData = JSON.parse(cleanJson);
} catch (e) {
  throw new Error('Failed to parse AI response as JSON: ' + e.message + '\n\nRaw (first 500 chars): ' + aiContent.substring(0, 500));
}

return {
  parsedData: parsedData,
  outputSheetName: outputSheetName
};

// Build all data for a single sheet with section dividers
const data = $('Parse AI Response').first().json.parsedData;
const input = $input.first().json;
const spreadsheetId = input.spreadsheetId || input.id;
const spreadsheetUrl = input.spreadsheetUrl || input.url;

const rows = [];
const sectionRows = [];
const tableHeaderRows = [];
const subHeaderRows = [];

function section(title) {
  sectionRows.push(rows.length);
  rows.push([title, '', '', '', '', '']);
}

function th() {
  tableHeaderRows.push(rows.length);
  var args = [];
  for (var i = 0; i < arguments.length; i++) args.push(arguments[i]);
  while (args.length < 6) args.push('');
  rows.push(args);
}

function sub(title) {
  subHeaderRows.push(rows.length);
  rows.push([title, '', '', '', '', '']);
}

function r() {
  var args = [];
  for (var i = 0; i < arguments.length; i++) args.push(arguments[i]);
  while (args.length < 6) args.push('');
  rows.push(args);
}

function empty() {
  rows.push(['', '', '', '', '', '']);
}

// ===== TITLE =====
section('SEO АУДИТ — ЗВІТ');
empty();

// ===== 1. GENERAL INFO =====
section('1. ЗАГАЛЬНА ІНФОРМАЦІЯ');
th('Параметр', 'Значення');
r('URL сайту', data.generalInfo?.siteUrl || '');
r('Дата аудиту', data.generalInfo?.auditDate || '');
r('Режим сканування', data.generalInfo?.scanMode || '');
r('Параметри для аналізу', data.generalInfo?.parametersAnalyzed || '');
empty();

// ===== 2. SUMMARY =====
section('2. ЗВЕДЕННЯ');
r('Перевірено URL', data.summary?.totalUrlsChecked || 0);
empty();
sub('Розподіл URL за типом');
th('Тип', 'Кількість', 'Відсоток');
(data.summary?.urlBreakdown || []).forEach(function(item) { r(item.type, item.count, item.percent); });
empty();
sub('URL з помилками');
r('Всього URL з помилками', data.summary?.urlsWithErrors?.total || 0, data.summary?.urlsWithErrors?.percent || '');
th('Тип помилки', 'Кількість', 'Відсоток');
(data.summary?.urlsWithErrors?.breakdown || []).forEach(function(item) { r(item.type, item.count, item.percent); });
empty();

// ===== 3. CONTENT TYPES =====
section('3. ТИПИ КОНТЕНТУ');
th('Тип контенту', 'Кількість', 'Відсоток');
(data.contentTypes || []).forEach(function(item) { r(item.type, item.count, item.percent); });
empty();

// ===== 4. URL STRUCTURE =====
section('4. СТРУКТУРА URL');
th('Хост', 'Сегмент', 'Кількість', 'Відсоток');
(data.urlStructure || []).forEach(function(item) { r(item.host, item.segment, item.count, item.percent); });
empty();

// ===== 5. SERVER RESPONSE CODES =====
section('5. КОДИ ВІДПОВІДЕЙ СЕРВЕРА');
th('Код', 'Опис', 'Кількість', 'Відсоток');
(data.serverResponseCodes || []).forEach(function(item) { r(item.code, item.description, item.count, item.percent); });
empty();

// ===== 6. INDEXABILITY =====
section('6. ІНДЕКСАЦІЯ');
sub('Відкритість для пошуку');
th('Статус', 'Кількість', 'Відсоток');
(data.indexability?.searchOpenness || []).forEach(function(item) { r(item.status, item.count, item.percent); });
empty();
sub('Причини закриття від індексації');
th('Причина', 'Кількість', 'Відсоток');
(data.indexability?.closureReasons || []).forEach(function(item) { r(item.reason, item.count, item.percent); });
empty();

// ===== 7. META ROBOTS & CANONICAL =====
section('7. META ROBOTS & CANONICAL');
sub('Meta Robots директиви');
th('Директива', 'Кількість', 'Відсоток');
(data.metaRobots || []).forEach(function(item) { r(item.directive, item.count, item.percent); });
empty();
sub('Використання canonical');
th('Статус', 'Кількість', 'Відсоток');
(data.canonicalUsage || []).forEach(function(item) { r(item.status, item.count, item.percent); });
empty();

// ===== 8. URL DEPTH =====
section('8. ГЛИБИНА ТА ВКЛАДЕНІСТЬ URL');
sub('Глибина (кліки від головної)');
th('Глибина', 'Кількість URL');
(data.urlDepth || []).forEach(function(item) { r(item.depth, item.count); });
empty();
sub('Вкладеність URL (сегменти)');
th('Вкладеність', 'Кількість URL');
(data.urlNesting || []).forEach(function(item) { r(item.nesting, item.count); });
empty();

// ===== 9. LOADING SPEED HTML =====
section('9. ШВИДКІСТЬ ЗАВАНТАЖЕННЯ HTML');
th('Метрика', 'Значення');
r('Максимальний час', (data.loadingSpeedHtml?.maxMs || 0) + ' мс');
r('Мінімальний час', (data.loadingSpeedHtml?.minMs || 0) + ' мс');
r('Медіана', (data.loadingSpeedHtml?.medianMs || 0) + ' мс');
empty();
sub('Розподіл за швидкістю');
th('Категорія', 'Кількість', 'Відсоток');
(data.loadingSpeedHtml?.distribution || []).forEach(function(item) { r(item.category, item.count, item.percent); });
empty();

// ===== 10. LOADING SPEED RESOURCES =====
section('10. ШВИДКІСТЬ ЗАВАНТАЖЕННЯ РЕСУРСІВ');
th('Метрика', 'Значення');
r('Максимальний час', (data.loadingSpeedResources?.maxMs || 0) + ' мс');
r('Мінімальний час', (data.loadingSpeedResources?.minMs || 0) + ' мс');
r('Медіана', (data.loadingSpeedResources?.medianMs || 0) + ' мс');
empty();
sub('Розподіл за швидкістю');
th('Категорія', 'Кількість', 'Відсоток');
(data.loadingSpeedResources?.distribution || []).forEach(function(item) { r(item.category, item.count, item.percent); });
empty();

// ===== 11. PROTOCOLS =====
section('11. ПРОТОКОЛИ');
sub('Протокол внутрішніх HTML');
th('Протокол', 'Кількість', 'Відсоток');
(data.protocols?.html || []).forEach(function(item) { r(item.protocol, item.count, item.percent); });
empty();
sub('Протокол зображень та ресурсів');
th('Протокол', 'Кількість', 'Відсоток');
(data.protocols?.resources || []).forEach(function(item) { r(item.protocol, item.count, item.percent); });
empty();

// ===== 12. SEO ELEMENTS =====
section('12. SEO-ЕЛЕМЕНТИ');
sub('Унікальність Title, Description, H1');
th('Елемент', 'Унікальні', 'Дублюються', 'Відсутні');
(data.seoElements?.uniqueness || []).forEach(function(item) { r(item.element, item.unique, item.duplicated, item.missing); });
empty();
sub('Довжина Title, Description, H1');
th('Елемент', 'Оптимальні', 'Короткі', 'Довгі');
(data.seoElements?.length || []).forEach(function(item) { r(item.element, item.optimal, item.short, item.long); });
empty();

// ===== 13. CONTENT METRICS =====
section('13. МЕТРИКИ КОНТЕНТУ');
sub('Кількість символів на сторінці');
th('Категорія', 'Кількість', 'Відсоток');
(data.contentMetrics?.charactersPerPage || []).forEach(function(item) { r(item.category, item.count, item.percent); });
empty();
sub('Кількість слів на сторінці');
th('Категорія', 'Кількість', 'Відсоток');
(data.contentMetrics?.wordsPerPage || []).forEach(function(item) { r(item.category, item.count, item.percent); });
empty();
sub('Розмір зображень');
th('Категорія', 'Кількість', 'Відсоток');
(data.contentMetrics?.imageSizes || []).forEach(function(item) { r(item.category, item.count, item.percent); });
empty();

// ===== 14. ERRORS =====
section('14. ПОМИЛКИ');
sub('Критичність помилок');
th('Рівень', 'Кількість');
r('Висока', data.errors?.criticality?.high || 0);
r('Середня', data.errors?.criticality?.medium || 0);
r('Низька', data.errors?.criticality?.low || 0);
empty();
sub('Топ помилок');
th('Помилка', 'Кількість', 'Відсоток');
(data.errors?.topErrors || []).forEach(function(item) { r(item.error, item.count, item.percent); });
empty();
sub('Помилки високої критичності');
th('Помилка', 'Приклад URL', 'Кількість');
(data.errors?.highCriticality || []).forEach(function(item) { r(item.error, item.exampleUrl, item.count); });
empty();
sub('Помилки середньої критичності');
th('Помилка', 'Приклад URL', 'Кількість');
(data.errors?.mediumCriticality || []).forEach(function(item) { r(item.error, item.exampleUrl, item.count); });
empty();
sub('Помилки низької критичності');
th('Помилка', 'Приклад URL', 'Кількість');
(data.errors?.lowCriticality || []).forEach(function(item) { r(item.error, item.exampleUrl, item.count); });
empty();

// ===== 15. SCAN SETTINGS =====
section('15. НАЛАШТУВАННЯ СКАНУВАННЯ');
th('Параметр', 'Значення');
r('Проскановано URL', data.scanSettings?.analyzedData?.scannedUrls || '');
r('Сегментація', data.scanSettings?.analyzedData?.segmentation || '');
r('Обрано параметрів', data.scanSettings?.analyzedData?.parametersSelected || '');
r('Макс. час очікування', data.scanSettings?.speedSettings?.maxTimeout || '');
r('JavaScript рендеринг', data.scanSettings?.speedSettings?.jsRendering || '');

return {
  spreadsheetId: spreadsheetId,
  spreadsheetUrl: spreadsheetUrl,
  rows: rows,
  sectionRows: sectionRows,
  tableHeaderRows: tableHeaderRows,
  subHeaderRows: subHeaderRows,
  totalRows: rows.length
};

// Build formatting requests for Google Sheets API
const sd = $('Build Sheet Data').first().json;
const spreadsheetId = sd.spreadsheetId;
const sectionRows = sd.sectionRows;
const tableHeaderRows = sd.tableHeaderRows;
const subHeaderRows = sd.subHeaderRows;
const totalRows = sd.totalRows;
const cols = 6;

const req = [];

// 0. Default font for all cells
req.push({
  repeatCell: {
    range: { sheetId: 0, startRowIndex: 0, endRowIndex: totalRows, startColumnIndex: 0, endColumnIndex: cols },
    cell: { userEnteredFormat: { textFormat: { fontFamily: 'Google Sans', fontSize: 10 }, verticalAlignment: 'MIDDLE' } },
    fields: 'userEnteredFormat(textFormat(fontFamily,fontSize),verticalAlignment)'
  }
});

// 1. Rename sheet
req.push({
  updateSheetProperties: {
    properties: { sheetId: 0, title: 'SEO Аудит' },
    fields: 'title'
  }
});

// 2. Column widths
var widths = [350, 200, 120, 120, 120, 120];
widths.forEach(function(w, i) {
  req.push({
    updateDimensionProperties: {
      range: { sheetId: 0, dimension: 'COLUMNS', startIndex: i, endIndex: i + 1 },
      properties: { pixelSize: w },
      fields: 'pixelSize'
    }
  });
});

// 3. Title row — first section row, extra large centered
if (sectionRows.length > 0) {
  var ti = sectionRows[0];
  req.push({ mergeCells: { range: { sheetId: 0, startRowIndex: ti, endRowIndex: ti + 1, startColumnIndex: 0, endColumnIndex: cols }, mergeType: 'MERGE_ALL' } });
  req.push({ repeatCell: {
    range: { sheetId: 0, startRowIndex: ti, endRowIndex: ti + 1, startColumnIndex: 0, endColumnIndex: cols },
    cell: { userEnteredFormat: {
      backgroundColor: { red: 0.102, green: 0.451, blue: 0.910 },
      textFormat: { bold: true, fontSize: 14, foregroundColor: { red: 1, green: 1, blue: 1 }, fontFamily: 'Google Sans' },
      horizontalAlignment: 'CENTER',
      verticalAlignment: 'MIDDLE'
    } },
    fields: 'userEnteredFormat(backgroundColor,textFormat,horizontalAlignment,verticalAlignment)'
  } });
  req.push({ updateDimensionProperties: {
    range: { sheetId: 0, dimension: 'ROWS', startIndex: ti, endIndex: ti + 1 },
    properties: { pixelSize: 44 },
    fields: 'pixelSize'
  } });
}

// 4. Section headers (skip title at index 0) — dark blue, white bold
sectionRows.slice(1).forEach(function(ri) {
  req.push({ mergeCells: { range: { sheetId: 0, startRowIndex: ri, endRowIndex: ri + 1, startColumnIndex: 0, endColumnIndex: cols }, mergeType: 'MERGE_ALL' } });
  req.push({ repeatCell: {
    range: { sheetId: 0, startRowIndex: ri, endRowIndex: ri + 1, startColumnIndex: 0, endColumnIndex: cols },
    cell: { userEnteredFormat: {
      backgroundColor: { red: 0.102, green: 0.451, blue: 0.910 },
      textFormat: { bold: true, fontSize: 11, foregroundColor: { red: 1, green: 1, blue: 1 }, fontFamily: 'Google Sans' },
      horizontalAlignment: 'LEFT',
      verticalAlignment: 'MIDDLE'
    } },
    fields: 'userEnteredFormat(backgroundColor,textFormat,horizontalAlignment,verticalAlignment)'
  } });
  req.push({ updateDimensionProperties: {
    range: { sheetId: 0, dimension: 'ROWS', startIndex: ri, endIndex: ri + 1 },
    properties: { pixelSize: 36 },
    fields: 'pixelSize'
  } });
});

// 5. Sub-headers — light blue bg, blue bold text, merged
subHeaderRows.forEach(function(ri) {
  req.push({ mergeCells: { range: { sheetId: 0, startRowIndex: ri, endRowIndex: ri + 1, startColumnIndex: 0, endColumnIndex: cols }, mergeType: 'MERGE_ALL' } });
  req.push({ repeatCell: {
    range: { sheetId: 0, startRowIndex: ri, endRowIndex: ri + 1, startColumnIndex: 0, endColumnIndex: cols },
    cell: { userEnteredFormat: {
      backgroundColor: { red: 0.910, green: 0.941, blue: 0.996 },
      textFormat: { bold: true, fontSize: 10, foregroundColor: { red: 0.102, green: 0.451, blue: 0.910 }, fontFamily: 'Google Sans' },
      horizontalAlignment: 'LEFT',
      verticalAlignment: 'MIDDLE'
    } },
    fields: 'userEnteredFormat(backgroundColor,textFormat,horizontalAlignment,verticalAlignment)'
  } });
  req.push({ updateDimensionProperties: {
    range: { sheetId: 0, dimension: 'ROWS', startIndex: ri, endIndex: ri + 1 },
    properties: { pixelSize: 30 },
    fields: 'pixelSize'
  } });
});

// 6. Table header rows — gray bg, bold text
tableHeaderRows.forEach(function(ri) {
  req.push({ repeatCell: {
    range: { sheetId: 0, startRowIndex: ri, endRowIndex: ri + 1, startColumnIndex: 0, endColumnIndex: cols },
    cell: { userEnteredFormat: {
      backgroundColor: { red: 0.945, green: 0.953, blue: 0.957 },
      textFormat: { bold: true, fontSize: 10, fontFamily: 'Google Sans' },
      horizontalAlignment: 'LEFT',
      verticalAlignment: 'MIDDLE'
    } },
    fields: 'userEnteredFormat(backgroundColor,textFormat,horizontalAlignment,verticalAlignment)'
  } });
});

// 7. Light horizontal grid lines across all data
req.push({ updateBorders: {
  range: { sheetId: 0, startRowIndex: 0, endRowIndex: totalRows, startColumnIndex: 0, endColumnIndex: cols },
  innerHorizontal: { style: 'SOLID', width: 1, color: { red: 0.918, green: 0.933, blue: 0.945 } }
} });

return {
  spreadsheetId: spreadsheetId,
  spreadsheetUrl: sd.spreadsheetUrl,
  formatRequests: req
};

// Calculate processing stats and return final response
const startTime = $('Build AI Request').first().json.timestamp;
const endTime = new Date().toISOString();
const processingTime = Math.round((new Date(endTime) - new Date(startTime)) / 1000);

const spreadsheetUrl = $('Build Sheet Data').first().json.spreadsheetUrl;
const totalRows = $('Build Sheet Data').first().json.totalRows;

return {
  success: true,
  spreadsheetUrl: spreadsheetUrl,
  sheetName: 'SEO Аудит',
  totalRows: totalRows,
  processingTime: processingTime
};
