// Helper function to extract data from MCP response
function extractMcpData(raw) {
  if (!raw) return null;
  // MCP returns: { content: [{ type: 'text', text: { ... actual data ... } }] }
  if (raw.content && Array.isArray(raw.content) && raw.content[0]) {
    const textContent = raw.content[0].text;
    if (typeof textContent === 'string') {
      try { return JSON.parse(textContent); } catch (e) { return textContent; }
    }
    return textContent;
  }
  return raw;
}

// Get stored data from previous nodes
const domain = $('Set Variables').first().json.domain;
const folderId = $('Create Folder').first().json.id;
const spreadsheetId = $('Create Spreadsheet').first().json.spreadsheetId;

// Parse API responses
let dr = null, backlinksStats = null, anchors = [], topPages = [], allBacklinks = [];

try {
  const drRaw = $('Get DR').first().json;
  dr = extractMcpData(drRaw);
  if (dr && dr.domain_rating && typeof dr.domain_rating === 'object') {
    dr = dr.domain_rating;
  }
} catch (e) { dr = { error: e.message }; }

try {
  const statsRaw = $('Get Backlinks Stats').first().json;
  backlinksStats = extractMcpData(statsRaw);
  if (backlinksStats && backlinksStats.stats) backlinksStats = backlinksStats.stats;
} catch (e) { backlinksStats = { error: e.message }; }

try {
  const anchorsRaw = $('Get External Anchors').first().json;
  const anchorsData = extractMcpData(anchorsRaw);
  anchors = anchorsData?.linkedanchors || anchorsData?.anchors || (Array.isArray(anchorsData) ? anchorsData : []);
} catch (e) { anchors = []; }

try {
  const pagesRaw = $('Get Top Pages').first().json;
  const pagesData = extractMcpData(pagesRaw);
  topPages = pagesData?.pages || pagesData?.top_pages || (Array.isArray(pagesData) ? pagesData : []);
} catch (e) { topPages = []; }

try {
  const blRaw = $('Get All Backlinks').first().json;
  const blData = extractMcpData(blRaw);
  allBacklinks = blData?.backlinks || (Array.isArray(blData) ? blData : []);
} catch (e) { allBacklinks = []; }

// === Build sheet data ===

// Sheet 1: Профіль беклінків
const profileRows = [
  ['ПРОФІЛЬ БЕКЛІНКІВ', '', ''],
  ['', '', ''],
  ['Метрика', 'Значення', 'Опис'],
  ['Domain Rating (DR)', dr?.domain_rating || 'N/A', 'Рейтинг домену від Ahrefs (0-100)'],
  ['Ahrefs Rank', dr?.ahrefs_rank || 'N/A', 'Позиція в глобальному рейтингу'],
  ['', '', ''],
  ['Беклінки (всього)', backlinksStats?.live || backlinksStats?.backlinks || 'N/A', 'Кількість активних беклінків'],
  ['Реферальні домени', backlinksStats?.live_refdomains || backlinksStats?.refdomains || 'N/A', 'Унікальні домени-донори'],
  ['Dofollow беклінки', backlinksStats?.live_dofollow || 'N/A', 'Беклінки без nofollow'],
  ['Nofollow беклінки', backlinksStats?.live_nofollow || 'N/A', 'Беклінки з nofollow'],
  ['', '', ''],
  ['Домен', domain, ''],
  ['Дата аналізу', new Date().toISOString().slice(0, 10), '']
];

// Sheet 2: Анкор лист
const anchorRows = [
  ['АНКОР ЛИСТ', '', ''],
  ['', '', ''],
  ['Анкор', 'Кількість доменів', 'Кількість посилань']
];
if (Array.isArray(anchors)) {
  anchors.slice(0, 50).forEach(a => {
    anchorRows.push([
      a.anchor || '',
      a.linked_domains || a.linkedDomains || 0,
      a.links_from_target || a.linksFromTarget || 0
    ]);
  });
}

// Sheet 3: ТОП сторінки
const pagesRows = [
  ['ТОП СТОРІНКИ ЗА ПОСИЛАННЯМИ', '', '', '', ''],
  ['', '', '', '', ''],
  ['URL', 'Реф. домени', 'UR', 'Трафік', 'Ключі']
];
if (Array.isArray(topPages)) {
  topPages.slice(0, 100).forEach(p => {
    pagesRows.push([
      p.url || '',
      p.referring_domains || p.referringDomains || 0,
      p.ur || 0,
      p.sum_traffic || p.sumTraffic || 0,
      p.keywords || 0
    ]);
  });
}

// Sheet 4: Всі беклінки
const blRows = [
  ['ВСІ БЕКЛІНКИ (ТОП-100 за DR)', '', '', '', '', ''],
  ['', '', '', '', '', ''],
  ['URL донора', 'DR джерела', 'Трафік', 'Анкор', 'Тип', 'Nofollow']
];
if (Array.isArray(allBacklinks)) {
  allBacklinks.slice(0, 100).forEach(b => {
    blRows.push([
      b.url_from || b.urlFrom || '',
      b.domain_rating_source || b.domainRatingSource || 0,
      b.traffic_domain || b.trafficDomain || 0,
      b.anchor || '',
      b.link_type || b.linkType || '',
      b.is_nofollow || b.isNofollow ? 'Yes' : 'No'
    ]);
  });
}

return [{
  json: {
    domain,
    folderId,
    spreadsheetId,
    profileRows,
    anchorRows,
    pagesRows,
    blRows,
    debug: {
      drRaw: dr,
      backlinksStatsRaw: backlinksStats,
      anchorsCount: anchors.length,
      pagesCount: topPages.length,
      backlinksCount: allBacklinks.length
    }
  }
}];

